def smooth_plot(pline, wsize):
    smoothed_pline = []
    for ipl in range(1, len(pline) + 1):
        smoothed_pline.append(sum(pline[max(0, ipl - wsize):ipl]) / min(wsize, ipl))
    return smoothed_pline

